import React from "react";
import classes from './ReminderSettings.module.css'

const ReminderSettings =  props =>{

    return(
        <div className={classes['reminder-div']}>
            <div className={classes['content-div']}>
                <div className={classes['rem-heading']}>Set reminder for the task:</div>
                <div className={classes['rem-desc']}>(Sets reminders for all sub-tasks as well)</div>
            </div>
            <div><input type="radio"  id="yes" className={classes['radiobutton']}/> <label className={classes['radiobutton-label']}>Yes</label></div>
            <div><input type="radio"  id="no" className={classes['radiobutton']}/> <label className={classes['radiobutton-label']}>No</label></div>
        </div>

    );
}

export default ReminderSettings;
