import React, { Fragment, useRef } from "react";
import './SubTask.css';
import SubtaskForm from "./SubtaskForm";
import AddSubTaskButton from "./AddSubTaskButton";

const SubTask = (props) => {
  const subTaskObject = useRef({});

  const onSaveSubtaskHandler = (subtaskObject)=>{
    const {SubTaskTitle} = subtaskObject;

    subTaskObject.current = {
      ...subTaskObject.current,
      [SubTaskTitle]: subtaskObject,
    };

    console.log(subTaskObject.current);
      
  }


  return (
    <Fragment>
      <div className='sub-task-items-div'>
        <SubtaskForm onSaveSubtask={onSaveSubtaskHandler} ></SubtaskForm>
        <SubtaskForm onSaveSubtask={onSaveSubtaskHandler}></SubtaskForm>
      </div>
      <AddSubTaskButton />
    </Fragment>
  );
};

export default SubTask;
