import React, { useState } from "react";
import classes from "./Popup.module.css";
import { Link } from "react-router-dom";

const Popup = (props) => {
    const [trigger,settrigger] = useState(props.trigger);


  return (
    trigger ?
    <div className={classes["pop-up-div"]}>
      <div className={classes["pop-up-div-inner"]}>
        <div className={classes["pop-up-heading"]}>
          Are you sure you want to mark the task as completed?
        </div>
        <div className={classes["pop-up-desc"]}>
          Task will not be visible once marked as completed.
        </div>
        <div className={classes.options}>   
          <button className={classes["save-button"]}>
            <Link to='/homepage' onClick={()=>settrigger(false)}className={classes["save-button-text"]}>Undo</Link>
          </button>
          <button className={classes["save-button"]}>
            <Link to='/homepage' style={{fontSize:"15px"}}className={classes["save-button-text"]}>Mark as completed</Link>
          </button>
        </div>
      </div>
    </div> :""
  );
};

export default Popup;
