import React, { useEffect } from "react";
import { Fragment, useRef, useState} from "react";
import { useParams } from "react-router-dom";
import EditTask from "./EditTask";
import AddSubtask from "../NewTaskPage/AddSubtask";
import Header from "../Header";
import TaskSettings from "../NewTaskPage/TaskSettings";
import ScrollTop from "../ScrollTop";
//import classes from "./SaveButton.module.css";
import { Link } from "react-router-dom";


const TaskDetailsPage = (props) => {

    const params = useParams();

    const [Task, setTask] = useState({})

    ///fetching the details of that particular task


  const createTask = useRef({
    newTask: "",
    subTasks: "",
    tasksettings: "",
    completed: "no",
  });



  const [startDate, setStartDate] = useState("");

  const createNewTaskHandler = (newtask) => {
    const { startDate } = newtask;
    setStartDate(startDate);
    createTask.current["newTask"] = newtask;
    console.log("from edit task page");
    console.log(newtask);
  };

  const createSubtaskHandler = (subtasks) => {
    createTask.current["subTasks"] = subtasks;
    console.log("from edit task page");
    console.log(subtasks);
  };

  const taskSettingsHandler = (tasksettings) => {
    createTask.current["tasksettings"] = tasksettings;
    console.log("from edit task page");
    console.log(tasksettings);
  };

  

  return (
    <Fragment>
      <Header />
      <EditTask onSaveNewTask={createNewTaskHandler} task={Task}/>
      <AddSubtask onSaveSubtask={createSubtaskHandler} sendDate={startDate} task={Task}/>
      <TaskSettings onSaveTaskSettings={taskSettingsHandler} task={Task}/>
      {/*<div className={classes["save-button-div"]}>
        <button className={classes["save-button"]} onClick={createTaskHandler}>
          <Link className={classes["save-button-text"]}>Save Task</Link>
        </button>
      </div>*/}
      <ScrollTop />
    </Fragment>
  );
};

export default TaskDetailsPage;
