import React, { useState } from "react";
import classes from "./NewTaskForm.module.css";

const NewTaskForm = (props) => {
  const [formErrors,setFormErrors] = useState({})
  const [newtask, setNewTask] = useState({
    taskTitle: "",
    taskDesc: "",
    deadline: "",
    startDate: "",
    startTime: "",
    days: "0",
    hours: "0",
    minutes: "0",
  });

  let name, value;

  const onChangeHandler = (e) => {
    name = e.target.name;
    value = e.target.value;

    const error = validateInput(name, value);

    if(error==""){
      setNewTask({ ...newtask, [name]: value });
    }
    else{

    }
    
  };


  const validateInput = (name,value) => {
    const errorObj = {}
    if(name=='taskTitle' && value.length>5){
      errorObj.taskTitle = "length has to be <=5";
    }
    return errorObj;
  }
  
  const onClickSaveHandler = (e) => {
    e.preventDefault();
    props.onSave(newtask);
  };

  return (
    <form>
      <div className={classes["new-task-inputs"]}>
        <div className={`${classes["text-input"]} ${classes["input-div"]}`}>
          <label className={classes["sub-heading"]}>Task Title:</label>
          <p> </p>
          <input
            className={classes["title-input"]}
            type="text"
            name="taskTitle"
            maxLength="5"
            onChange={onChangeHandler}
            value={newtask.taskTitle}
          />
        </div>
        <div className={`${classes["text-input"]} ${classes["input-div"]}`}>
          <label className={classes["sub-heading"]}>
            Task Description (optional):
          </label>
          <textarea
            className={classes["task-desc-input"]}
            rows="7"
            cols="50"
            name="taskDesc"
            onChange={onChangeHandler}
            value={newtask.taskDesc}
          ></textarea>
        </div>
        <div className={classes["input-div"]}>
          <label className={classes["sub-heading"]}>
            Select Task Deadline:
          </label>
          <input
            className={classes["date-input"]}
            type="date"
            name="deadline"
            onChange={onChangeHandler}
            value={newtask.deadline}
          />
        </div>
        <div className={classes["input-div"]}>
          <label className={classes["sub-heading"]}>Select Start Date:</label>
          <input
            className={classes["date-input"]}
            type="date"
            name="startDate"
            onChange={onChangeHandler}
            value={newtask.startDate}
          />
        </div>
        <div className={classes["input-div"]}>
          <label className={classes["sub-heading"]}>Select Start Time:</label>
          <input
            className={classes["date-input"]}
            type="time"
            name="startTime"
            onChange={onChangeHandler}
            value={newtask.startTime}
          />
        </div>

        <div className={`${classes["text-input"]} ${classes["input-div"]}`}>
          <label className={classes["sub-heading"]}>
            Select time required to complete:
          </label>
          <div className={classes["time-req-div"]}>
            <div className={classes["time-req-label"]}>Days: </div>
            <input
              className={classes["time-req-input"]}
              type="number"
              name="days"
              onChange={onChangeHandler}
              value={newtask.days}
            />
          </div>

          <div className={classes["time-req-div"]}>
            <div className={classes["time-req-label"]}>Hours: </div>
            <input
              className={classes["time-req-input"]}
              type="number"
              value={newtask.hours}
              name="hours"
              onChange={onChangeHandler}
            />
          </div>

          <div className={classes["time-req-div"]}>
            <div className={classes["time-req-label"]}>Minutes: </div>
            <input
              className={classes["time-req-input"]}
              type="number"
              value={newtask.minutes}
              name="minutes"
              onChange={onChangeHandler}
            />
          </div>
        </div>
       <button className={classes['task-save-button']} onClick={onClickSaveHandler}>Save</button>
      </div>
    </form>
  );
};

export default NewTaskForm;
