import React, { Fragment, useState } from "react";
import './SubTask.css';
import SubtaskForm from "./SubtaskForm";

const SubTask = (props) => {
    const [subtask, setsubtask] = useState({});

  const onSaveSubtaskHandler = (subtaskObject)=>{
      const newsubtask = {
        ...subtaskObject,
        id:'subtask1'
      }
      console.log(newsubtask);

      setsubtask((prev)=>({
        ...prev,
        [newsubtask.id]:newsubtask
      }))
      
  }


  return (
    <Fragment>
      <div className='sub-task-items-div'>
        <SubtaskForm onSaveSubtask={onSaveSubtaskHandler}></SubtaskForm>
        <SubtaskForm></SubtaskForm>
      </div>
    </Fragment>
  );
};

export default SubTask;
