import React, { Fragment } from "react";
import classes from "./AddTask.module.css";
import NewTaskForm from "./NewTaskForm";

const AddTask = (props) => {

  const isObjectEmpty = (obj) => {
    for (let key in obj) {
      if (obj.hasOwnProperty(key)) {
        return false;
      }
    }
    return true;
  };

    const onSaveHandler = (addTaskData, errorObj)=>{
      console.log(errorObj)
      console.log(!isObjectEmpty(errorObj))
        if(isObjectEmpty(errorObj)){
          
          console.log('invalid inputs. please try again');
        }
        else{
          props.onSaveNewTask(addTaskData);
        }
        
    }

  return (
    <Fragment>
      <div className={classes.newTaskDiv}>
        <div className={classes.heading}>Add a new task</div>
        <div className={classes.newTaskTile}>
          <NewTaskForm onSave={onSaveHandler}/>
        </div>
      </div>
    </Fragment>
  );
};

export default AddTask;
