import React, { useState } from "react";
import classes from "./LandingPageHeader.module.css";

const LandingPageHeader = (props) => {
  const [isHover, setIsHover] = useState(false);

  const HandleMouseEnter = () => {
    setIsHover(true);
  };
  const HandleMouseLeave = () => {
    setIsHover(false);
  };

  return (
    <div className={classes.headerdiv}>
      <div className={classes.header}>
        <h1 className={classes.brand}>TaskPlanr</h1>
        <button  onMouseEnter={HandleMouseEnter} onMouseLeave={HandleMouseLeave} className={`${classes.button} `} ><a href="">Join Now</a></button>
      </div>
    </div>
  );
};

export default LandingPageHeader;
