import React from "react";
import classes from "./OngoingTile.module.css";
import { RiPencilFill, RiDeleteBin5Line } from "react-icons/ri";
import ProgressBar from "./ProgressBar";

const PendingTile = (props) => {
  const subtasksTotal = 4;
 

  return (
    <div className={classes.tile}>
      <div className={classes.tileBody}>
        <div className={classes.tileHeader}>
          <div className={classes.taskName}>Completing Project</div>
          <div className={classes.options}>
            <div className={classes.priorityBox}>Priority: 1</div>
            <RiPencilFill className={classes.optionIcon} />
            <RiDeleteBin5Line className={classes.optionIcon} />
          </div>
        </div>
        <div className={classes.bodyTitle}>
          Total sub-tasks: {subtasksTotal}
        </div>
        <div className={classes.bodyTitle}>
          Estimated time required: {estimatedTime}
        </div>
        <div className={classes.bodyTitle}>Reminders: ON</div>
        <div className={classes.dueDate}>DUE DATE: 20 JUNE</div>
        <div className={classes.completedCheckBox}>
          <input
            type="checkbox"
            name="completed"
            id="completed"
            value="isCompleted"
            className={classes.checkBox}
          />
          <label for="completed" className={classes.checkBoxLabel}>Mark as completed</label>
        </div>
      </div>
    </div>
  );
};

export default PendingTile;
