import React from "react";
import classes from './TaskTiles.module.css'
import OngoingTile from "./OngoingTile";
import { Link } from "react-router-dom";

const TaskTiles = props =>{

    const isMore = props.moreButton;

    return(
        <div className={classes.taskContainer}>
            <div className={classes.titleContainer}>
                <div className={classes.title}>
                    Ongoing Tasks
                </div>
                <div  className={`${classes.buttondiv} ${!isMore && classes.morebuttonHide}`}>
                {props.ongoingTasks.length!==0 && <Link to="/tasks" className={classes.morebutton} >more</Link>}
                </div>
            </div>
            <div className={classes.tilesContainer}>
                <OngoingTile></OngoingTile>
                <OngoingTile></OngoingTile>
            </div>
        </div>

    );
}

export default TaskTiles;
