import React, { useEffect } from "react";
import classes from './Stats.module.css';
import Calendar from "./Calendar";
import Progress from './Progress';

const Stats = props =>{

    useEffect(()=>{console.log(props.totalBacklog)
    console.log(props.totalTasks)},[])

    return(
        <div className={classes.statsContainer}>
            <Calendar></Calendar>
            <Progress totalTasks={props.totalTasks} totalBacklog={props.totalBacklog}></Progress>
        </div>

    );
}

export default Stats;
