import React, { Fragment } from "react";
import classes from "./SignupPage.module.css";
import styles from "../NewTaskPage/AddTask.module.css";
import LandingPageHeader from "../LandingPage/LandingPageHeader";
import LoginSignupButton from "../LoginSignupButton";
import ScrollTop from "../ScrollTop";

const SignupPage = (props) => {
  const [credentials, setCredentials] = useState({
    email:"",
    password:"",
    repassword:""
  });

  return (
    <Fragment>
      <LandingPageHeader joinButton={false}> </LandingPageHeader>
      <div className={classes["create-account-tile"]}>
        <div className={styles.heading}>Create Account</div>
        <div className={classes['create-account-body']}>
            <div>
                <div className={classes['input-heading']}> Enter email:</div>
                <input className={classes['input-text']} type='email'onChange={OnInputHandler}/>
            </div>
            <div>
                <div className={classes['input-heading']}> Enter password:</div>
                <input className={classes['input-text']} type='password' onChange={OnInputHandler}/>
            </div>
            <div>
                <div className={classes['input-heading']}> Re-enter password:</div>
                <input className={classes['input-text']} type='password'/>
            </div>
            <LoginSignupButton/>
            <p>error messages</p>
        </div>
      </div>
      <ScrollTop/>
    </Fragment>
  );
};

export default SignupPage;
