import React, { Fragment } from "react";
import classes from "./AddTask.module.css";
import Header from "../Header";

const AddTask = (props) => {
  return (
    <Fragment>
      <Header />
      <div className={classes.newTaskDiv}>
        <div className={classes.heading}>Add a new task</div>
        <div className={classes.newTaskTile}>
          {/* <div className={classes.inputTile}>
                <div className={classes.subHeading}>Task Title: </div>
                <textarea rows="1" cols="40" className={classes.titleInput}></textarea>
            </div>
            <div className={classes.inputTile}>
                <div className={classes.subHeading}>Task Title: </div>
                <textarea rows="5" cols="40"></textarea>
            </div> */}

          <form>
            <label>
              Enter your name:
              <input
                type="text"
                value={name}
                onChange={(e) => setName(e.target.value)}
              />
            </label>
          </form>
        </div>
      </div>
    </Fragment>
  );
};

export default AddTask;
