import React, { Fragment, useState , useEffect} from "react";
import classes from "../NewTaskPage/AddTask.module.css";
import EditTaskForm from "./EditTaskForm";

const EditTask = (props) => {

    const [Task, setTask] = useState({});

    useEffect(() => {
        setTask(props.task)
      }, [props.task]);

    const onSaveHandler = (addTaskData)=>{
          props.onSaveNewTask(addTaskData);  
    }

  return (
    <Fragment>
      <div className={classes.newTaskDiv}>
        <div className={classes.heading}>{props.title}</div>
        <div className={classes.newTaskTile}>
          <EditTaskForm onSave={onSaveHandler} task={props.Task}/>
        </div>
      </div>
    </Fragment>
  );
};

export default EditTask;
